<?php		## Güvenlik ##	echo !defined("GUVENLIK") ? die("Erisim Engellendi.") : null;	## IP Adresi ##	function IP(){		if(getenv("HTTP_CLIENT_IP")) {			$ip = getenv("HTTP_CLIENT_IP");		} elseif(getenv("HTTP_X_FORWARDED_FOR")) {			$ip = getenv("HTTP_X_FORWARDED_FOR");			if (strstr($ip, ',')) {				$tmp = explode (',', $ip);				$ip = trim($tmp[0]);			}		} else {		$ip = getenv("REMOTE_ADDR");		}		return $ip;	}		## POST Method ##	function p($par, $st = false){		if ($st){			return htmlspecialchars(addslashes(trim($_POST[$par])));		}else {			return addslashes(trim($_POST[$par]));		}	}		## GET Method ##	function g($par){		return trim(strip_tags(addslashes(mysql_real_escape_string($_GET[$par]))));	}		## Kısalt Fonksiyonu ##	function kisalt($par, $uzunluk = 50){		if (strlen($par) > $uzunluk){			$par = mb_substr($par, 0, $uzunluk, "UTF-8")."..";		}		return $par;	}		## GO Fonksiyonu ##	function go($par, $time = 0){		if ($time == 0){			header("Location: {$par}");		}else {			header("Refresh: {$time}; url={$par}");		}	}		## Session Fonksiyonu ##	function session($par){		if ($_SESSION[$par]){			return $_SESSION[$par];		}else {			return false;		}	}		## Cookie Fonksiyonu ##	function cookie($par){		if ($_COOKIE[$par]){			return $_COOKIE[$par];		}else {			return false;		}	}		## Temizleme Fonksiyonu ##	function ss($par){		return stripslashes($par);	}		## Session Oluşturma Fonksiyonu ##	function session_olustur($par){		foreach ($par as $anahtar => $deger){			$_SESSION[$anahtar] = $deger;		}	}		## Sef Link Fonksiyonu ##	function sef_link($baslik){		$bul = array('Ç', 'Ş', 'Ğ', 'Ü', 'İ', 'Ö', 'ç', 'ş', 'ğ', 'ü', 'ö', 'ı', '-');		$yap = array('c', 's', 'g', 'u', 'i', 'o', 'c', 's', 'g', 'u', 'o', 'i', ' ');		$perma = strtolower(str_replace($bul, $yap, $baslik));		$perma = preg_replace("@[^A-Za-z0-9\-_]@i", ' ', $perma);		$perma = trim(preg_replace('/\s+/',' ', $perma));		$perma = str_replace(' ', '-', $perma);		return $perma;	}		## Query Fonksiyonu ##	function query($query){		return mysql_query($query);	}		## ROW Fonksiyonu #3	function row($query){		return mysql_fetch_array($query);	}		## ROWS Fonksiyonu ##	function rows($query){		return mysql_num_rows($query);	}?>